```cpp
typedef long long LL;
const int N=46341,P=4793;

int n,tot,p[P],Mo[N];
LL Ans;
bitset<N> v;

void Mob(int n) {
	Mo[1]=1;
    for (int i=2;i<=n;i++) {
        if (v[i]==0)
            Mo[i]=-1,p[++tot]=i;
        for (int j=1;j<=tot && i*p[j]<=n;j++) {
            v[i*p[j]]=1;
            if (i%p[j]) Mo[i*p[j]]=-Mo[i];
			else {
				Mo[i*p[j]]=0;
				break;
			}
        }
    }
}

int Calc(int s,int x) {
	int Sum=0;
	for (int l=s+1,r;l<(s<<1);l=r+1) {
		if (!(x/l)) return Sum;
		r=min(x/(x/l),(s<<1)-1);
		Sum+=(r-l+1)*(x/l);
	}
	return Sum;
}

int main() {
	scanf("%d",&n);
	int m=sqrt(n);
	Mob(m);
	for (int i=1;i<=m;i++)
		if (Mo[i]) {
			for (int j=1;j<=m/i;j++)
				Ans+=Mo[i]*Calc(j,n/(i*i*j));
		}
	printf("%lld",Ans);
	return 0;
}
