#include <iostream>
#include <cstring>
using namespace std;

struct dic_tree_node
{
	dic_tree_node * children[26];

	void init ()
	{
		for (int i = 0; i < 26; i++)
			children[i] = NULL;
	}
};

struct dic_tree
{
	dic_tree_node * root;

	void init ()
	{
		root = new dic_tree_node;
	}
} Tree;

char board[4][9] = {
	"********",
	"********",
	"********",
	"********"
};

void insert_to_dic_tree (char * word)
{
	const int len = strlen (word);
	int ind = 0;
	dic_tree_node * Node = Tree.root;

	while (ind < len)
	{
		if (Node->children[word[ind] - 'A'] == NULL)
		{
			Node->children[word[ind] - 'A'] = new dic_tree_node;
			Node->children[word[ind] - 'A']->init ();
		}
		Node = Node->children[word[ind] - 'A'];
		ind++;
	}
	return;
}

void put_into_board (char ch)
{
	int index = ch - 'A' + 3;
	int row = index / 8, col = index % 8;

	board[row][col] = ch;
}

void printboard ()
{
	for (int i = 0; i < 4; i++)
		cout << board[i] << endl;
}

int main()
{
	Tree.init();

	int str_count;
	cin >> str_count;
	char str[101];

	for (int i = 0; i < str_count; i++)
	{
		cin >> str;
		insert_to_dic_tree (str);
	}

	cin >> str;
	dic_tree_node *Node = Tree.root;
	int ind = 0, len = strlen (str);
	while (ind < len)
	{
		if (Node == NULL)
		{
			printboard ();
			return 0;
		}

		Node = Node->children[str[ind] - 'A'];
		ind++;
	}

	for (int i = 0; i < 26; i++)
		if (Node->children[i] != NULL)
			put_into_board (i + 'A');
	printboard ();
	return 0;
}
