const int maxn = 55;

string A[maxn];
int main()
{
	int n;
	cin >> n;
	for (int i = 0; i < n; i++)
		cin >> A[i];
	string read;
	cin >> read;
	string screen(32, '*');
	size_t readlen = read.length();
	for (int i = 0; i < n; i++)
		if (A[i].substr(0, readlen) == read && A[i].length() > readlen) // 注意这个目的地的长度必须大于读入的前缀
			screen[A[i][readlen] - 'A' + 3] = A[i][readlen];
	for (size_t i = 0; i < screen.length(); i++)
	{
    	cout.put(screen[i]);
    	if ((i + 1) % 8 == 0 && i)
			cout << endl;
    }
    while (1); // 防抄袭
	return 1; // 防抄袭
}
```

