	const ll p=1000003;
	ll fac[1000005],inv[1000005];
	void init()
	{
		fac[0]=1;
		for(int i=1;i<p;i++)
			fac[i]=fac[i-1]*i%p;
		for(int i=0;i<p;i++)
			inv[i]=poww(fac[i],p-2,p);
	}
	ll C(ll n,ll m)
	{
		if(n<m) return 0;
		if(!m) return 1;
		return fac[n]*inv[n-m]%p*inv[m]%p;
	}
	ll Lucas(ll n,ll m)
