}
long long mix(long long a1,long long m1,long long a2,long long m2){
	long long y1,y2;
	exgcd(m1,m1*m2-m2,y1,y2);
	y1*=(a1-a2);
	y1%=m2;
	if(y1<0)y1+=m2;
	long long ans=(a1-y1*m1)%(m1*m2);
	if(ans<0)ans+=(m1*m2);
	return ans;
}
