#include <iostream>
#include <cstring>
#include <cstdio>
#include <algorithm>

const int maxn = 30003;
typedef unsigned long long ULL;
const ULL base = 131;
const int Mod = 1e9+7;

using namespace std;

int n, l, s, Ans;
ULL hs1[maxn][233], hs2[maxn][233], hs[maxn];
char ch[maxn][233];

inline void init(int x) {
	for(register int i=1; i<=l; i++) {
		hs1[x][i] = hs1[x][i-1] * 131 + ch[x][i];
	}
	for(register int i=l; i>=1; i--) {
		hs2[x][i] = hs2[x][i+1] * 137 + ch[x][i];
	}
}

int main() {
	scanf("%d%d%d", &n, &l, &s);
	for(register int i=1; i<=n; i++) {
		scanf("%s", ch[i]+1);
		init(i);
	}
	for(register int i=1; i<=l; i++) {
		for(register int j=1; j<=n; j++) {
			hs[j] = hs1[j][i-1]*233 + hs2[j][i+1]*211;
		}
		sort(hs+1, hs+1+n);
		int ans = 1;
		for(register int j=1; j<n; j++) {
			if(hs[j] == hs[j+1]) Ans += ans, ans ++;
			else ans = 1;
		}
	}
	printf("%d", Ans);
}
```

好多人写单哈希被卡了？

这里有一种**不会被卡哈希**的办法．

设$pw_i=base^i$，则传统哈希的哈希值$hash=\sum pw_i*s[i]$，但我这里的哈希是将$pw_i$换成一个$long \ long$范围内的随机数，这样就可以防卡哈希了．

具体可以看代码里函数$Init()$那部分．

```cpp
#include<bits/stdc++.h>
using namespace std;
#define N 30007
#define M 207
#define ll long long
const int lim=200;
const ll Max=1e15;
const ll base=131;
char s[N][M];
ll pw[M],hs[N];
ll a[N];
ll rn(ll x,ll y)
{
	return ((ll)rand()<<31|rand())%(y-x+1)+x;
}
void Init()
{
	pw[0]=1;
	for(int i=0;i<=lim;i++)
		pw[i]=rn(1,Max);
	//这里本来应该是 pw[i]=pw[i-1]*base;
}
int main()
{
	int n,m,x;
	srand((unsigned)time(NULL));
	Init();
	scanf("%d%d%d",&n,&m,&x);
	for(int i=1;i<=n;i++)
		scanf("%s",s[i]);
	for(int i=1;i<=n;i++)
	{
		for(int j=0;j<m;j++)
			hs[i]=hs[i]+pw[j]*s[i][j];
	}
	ll ans=0;
	for(int i=0;i<m;i++)
	{
		for(int j=1;j<=n;j++)
		{
			ll x=hs[j]-pw[i]*s[j][i];
			a[j]=x;
		}
		sort(a+1,a+n+1);
		ll cnt=0;
		for(int j=1;j<=n;j++)
		{
			if(j==1||a[j]!=a[j-1])
				ans+=cnt*(cnt-1)/2,cnt=1;
			else cnt++;
		}
		ans+=cnt*(cnt-1)/2;
	}
	printf("%lld\n",ans);
	return 0;
}
```

夏令营讲了，回来巩固一下
```
1.预处理，算出前i段的哈希值（这里使用的为进制哈希）
2.根据题意，枚举不同位是哪一位，
  求所有字符串在去掉这一位后的哈希值，
  用排序等方法统计相同的对数。
3.O(lnlog2n)
```
```
那么如何求去掉一位的哈希值呢？

设去掉第k位,长度为l

HS=hs[i-1]*base^(l-i)+hs[l]-hs[i]*base^(l-i);
```

```cpp
#include<bits/stdc++.h>

#define N 30105
#define ull unsigned long long

using namespace std;

ull hsi[N][250],base=233,mod=2123,hs[N];
ull pw[N];
int n,l,s,ans;
char ss[N][250];

void init(){
	pw[0]=1; 
	for(int i=1;i<=l;i++) pw[i]=pw[i-1]*base;
}//预处理进制

int main()
{
	scanf("%d%d%d",&n,&l,&s);
	for(int i=1;i<=n;i++) cin>>ss[i];
	init();
	for(int i=1;i<=n;i++){
		for(int j=1;j<=l;j++){
			hsi[i][j]=hsi[i][j-1]*base+ss[i][j-1];
		}
	}//求解哈希值
	for(int i=1;i<=l;i++){
		for(int j=1;j<=n;j++){
			hs[j]=hsi[j][i-1]*pw[l-i]+hsi[j][l]-hsi[j][i]*pw[l-i];
		}
		sort(hs+1,hs+1+n);
		int t=1;
		for(int j=1;j<=n;j++){
			if(hs[j]!=hs[j-1]) t=1;
			else ans+=t,++t;
		}//sort+模拟
	}cout<<ans;
	return 0;
}
```

