const int N = 1e2 + 10;
string s;
string ans[N];
int cnt,len;
int check(int k , char ch) // k -> |S| + 1,ch -> N|N|.
{
	for(int i = len - 1;i > k;i--) // 验证S|N| 到 S|S| 均不等于 N|N|
		if(s[i] == ch)
			return false;
	return true;
}
int main()
{
	cin >> s;
	len = s.size();
	if(s[len - 1] == '4')
	{
		ans[++cnt] = s; // 与原三角形仅有最后一位编号不同 
		ans[cnt][len - 1] = '1';
		ans[++cnt] = s;
		ans[cnt][len - 1] = '2';
		ans[++cnt] = s;
		ans[cnt][len - 1] = '3';
	}
	else
		for(int i = len - 1;i >= 1;i--) // 小三角形到大三角形地枚举，当然也可以正着枚举
			if( check( i , s[i] ) )
			{
