bool AtEdge(Point ob)//在边界，这条线就画完了
{return ob.x==1 || ob.x==b+1 || ob.y==1 || ob.y==a+1;}

int DFS(Point now)
{
	if(visit[now.y][now.x]) return 0;
	visit[now.y][now.x]=1;
	//Print_map(now);
	if(AtEdge(now))
	{
		visit[now.y][now.x]=0;
		return 1;
	}
	int cnt=0;
	for(short i=0;i<4;i++)
	{
		Point rear=now;
		rear.Walk(i);
		cnt+=DFS(rear);
	}
	visit[now.y][now.x]=0;
	return cnt;
}
