#include<cstdio>
using namespace std;
const int Maxn=100010;
long long a[Maxn],b;
int main(){
	int n;
    long long p=0,q=0;  //分别维护正数的和以及负数和的绝对值
	scanf("%d",&n);
	scanf("%lld",&a[1]);  //要先读入第一个数，因为第一个数没有差分
	for(int i=2;i<=n;i++){
		scanf("%lld",&a[i]);  //读入
		b=a[i]-a[i-1];  //你可以理解为一个临时的差分序列
		if(b>=0)p+=b;
		if(b<0)q+=-b;
	}
	printf("%lld\n",max(p,q));  //输出最少操作次数
	printf("%lld\n",p>q?p-q+1:q-p+1);  //输出结果数，即|p-q|+1
	return 0;
}

```

