	for(int i=1; i<=n; i++)
	{
		scanf("%d",&a[i]);
		if(i!=1)
		{
			b[i]=a[i]-a[i-1];
			if(b[i]>0)
				s1+=b[i];
			else
				s2-=b[i];
		}
	}
