#include<bits/stdc++.h>
//#define int long long
#define pair pair<int,int>
using namespace std;
inline void end()
{
	puts("");
	system("pause");
}
inline int read()
{
	int s=0,w=1;
	char ch=getchar();
	while(ch<'0'||ch>'9'){if(ch=='-') w=-1;ch=getchar();}
	while(ch>='0'&&ch<='9') s=s*10+ch-'0',ch=getchar();
	return s*w;
}
const int N=222,M=2e4+4,inf=0x3f3f3f3f,INF=1e9;
int s,t,maxf,minc;
int first[N],nex[M],to[M],w[M],c[M],num=1;
inline void add(int u,int v,int val,int cos)
{
	nex[++num]=first[u];
	first[u]=num;
	to[num]=v;
	w[num]=val;
	c[num]=cos;
}
inline void Add(int u,int v,int val,int cos)
{
	add(u,v,val,cos);
	add(v,u,0,-cos);
}
int dis[N],vis[N],cur[N];
inline bool SPFA()
{
	memset(dis,0x3f,sizeof(dis));
	memset(vis,0,sizeof(vis));
	deque<int> q;
	q.push_back(s);
	vis[s]=1;dis[s]=0;
	while(!q.empty())
	{
		int u=q.front();q.pop_front();vis[u]=0;
		for(int i=first[u];i;i=nex[i])
		{
			int v=to[i];
			if(!w[i]) continue;
			if(dis[v]>dis[u]+c[i])
			{
				dis[v]=dis[u]+c[i];
				if(!vis[v])
				{
					vis[v]=1;
					if(q.empty()||dis[v]>dis[q.front()]) q.push_back(v);
					else q.push_front(v);
				}
			}
		}
	}
	if(dis[t]==inf) return 0;
	return 1;
}
int dfs(int u,int in)
{
	if(u==t) return in;
	vis[u]=1;
	int out=0;
	for(int i=cur[u];i;i=nex[i])
	{
		int v=to[i];
		if(!w[i]||vis[v]) continue;
		if(dis[v]==dis[u]+c[i])
		{
			int res=dfs(v,min(w[i],in-out));
			w[i]-=res;
			w[i^1]+=res;
			out+=res;
			minc+=c[i]*res;
			if(in==out) break;
		}
	}
	vis[u]=0;
	if(!out) vis[u]=1;
	return out;
}
void Dinic()
{
	while(SPFA())
	{
		memcpy(cur,first,sizeof(first));
		while(1)
		{
			int detal=dfs(s,1e9);
			if(!detal) break;
			maxf+=detal;
		}
	}
}
int n,m,v[N],ex;
main()
{
	n=read(),m=read();ex=2*n+1;s=0,t=ex+1;
  //ex为额外点
	Add(s,ex,m,0);
	for(int i=1;i<=n;++i)
	{
		v[i]=read();
		Add(i+n,t,v[i],0);
		Add(ex,i+n,INF,0);
	}
	for(int i=1;i<=n;++i)
	{
		Add(s,i,v[i],0);
		for(int j=i+1;j<=n;++j)
		{
			int val=read();
			if(val==-1) continue;
			Add(i,j+n,INF,val);
		}
	}
	Dinic();//最小费用最大流
	printf("%d",minc);
	//end();
	return 0;
}
