}
int main()
{
	cin>>l>>r;
	n=r-l+1;
	find_key();
	long long ans=m;
	for(register int i=2;i<=m;i++)
	{
		ans*=i;
		ans%=mod;
	}
	for(register int i=m+2;i<=n+1;i++)
