*/

#include <bits/stdc++.h>
using namespace std;

//#pragma GCC optimize(3)
//#define int long long

using uint = unsigned int;
using ll = long long;
using ull = unsigned long long;
using db = double;
using ld = long double;

const int N=77;
const int S=26;

int sz,al,son[N][S],fa[N],ed[N];
void ins(string s){
	int p=0;
	for(char it:s){
		if(!son[p][it-'a'])son[p][it-'a']=++sz;
		p=son[p][it-'a'];
	} ed[p]=1;
}
void build(){
	queue <int> q;
	for(int i=0;i<al;i++)if(son[0][i])q.push(son[0][i]);
	while(!q.empty()){
		int t=q.front(); q.pop();
		for(int i=0;i<al;i++)
			if(son[t][i])q.push(son[t][i]),fa[son[t][i]]=son[fa[t]][i];
			else son[t][i]=son[fa[t]][i];
		ed[t]|=ed[fa[t]];
	}
}
struct mat{
	ld a[N][N];
	friend mat operator * (mat x,mat y){
		mat z; mem(z.a,0);
		for(int i=0;i<=sz;i++)
			for(int j=0;j<=sz;j++)
				for(int k=0;k<=sz;k++)
					z.a[i][j]+=x.a[i][k]*y.a[k][j];
		return z;
	}
}base,ans;

int n,len;
int main(){
	cin>>n>>len>>al;
	for(int i=0;i<n;i++){
		string s;
		cin>>s,ins(s);
	} build();
	for(int i=0;i<=sz;i++)
		for(int j=0;j<al;j++){
			int p=son[i][j];
			if(ed[p])base.a[i][0]+=1.0/al,base.a[i][sz+1]+=1.0/al;
			else base.a[i][p]+=1.0/al;
		}
	sz++,ans.a[0][0]=base.a[sz][sz]=1;
	while(len){
		if(len&1)ans=ans*base;
		base=base*base,len>>=1;
	} printf("%.10Lf\n",ans.a[0][sz]);
	return 0;
}
