ACAM ac;
int n, len, ab;
db one = 1.0;
int main()
{
	scanf("%d%d%d", &n, &len, &ab); string tmp;
	for (int i=1; i<=n; i++){cin >> tmp; ac.insert(tmp);}
	ac.build();
	int S = ac.size();
	for (int i=0; i<=S; i++)
		for (int j=0; j<ab; j++)
		{
			int k = ac.tr[i][j];
			if (ac.mark[k]){D[S+1][i] += one/ab; D[0][i] += one/ab;}
			else D[k][i] += one/ab;
		}
	D[S+1][S+1] = one; D ^= len;
	ans[0][0] = 1; D *= ans; // !!!!!
	printf("%.10f\n", D[S+1][0]);
	return 0;
}
