int n, k, a[1010];
map <int, int> cnt;

int main() {
	read(n, k); rer(i, 1, n, a);
	rep1(i, 1, n) {
		rep1(j, 1, sqrt(a[i])) if (a[i] % j == 0) {
			++cnt[j];
			if (j * j != a[i]) ++cnt[a[i] / j];
		}
	}
	int ans = 1;
	for (auto v : cnt) if (v.snd >= k && v.fst > ans) ans = v.fst;
	printf("%d", ans);
	rout;
}
