#include <bits/stdc++.h>
#define re register
#define mod 9909983
using namespace std;
inline int read(){}
inline void write(int x){}
int n,maxx,p;
int num[mod+10],number[mod+10];
inline void jkl(int x){
	for(re int i=1;i<=sqrt(x);i++)
	if(x%i==0)
	{
		int t=i;
		if(number[t%mod]==0){
			number[t%mod]=i;
			num[t%mod]=1;
		}
		else{
			while(number[t%mod]!=i)t++;
			num[t%mod]++;
		}

		t=x/i;
		if(number[t%mod]==0){
			number[t%mod]=x/i;
			num[t%mod]=1;
		}
		else{
			while(number[t%mod]!=x/i)t++;
			num[t%mod]++;
		}
	}
}
int main()
{
	n=read();p=read();
	for(re int i=1;i<=n;i++)jkl(read());
	for(re int i=1;i<mod;i++)
	if(num[i]>=p)maxx=max(maxx,number[i]);
	write(maxx);
	return 0;
}
