                    f[i+1][j+1][k+1][l+1][1]=min(f[i+1][j+1][k+1][l+1][1],tmp+(1<<i+1));
                    f[i+1][j][k+1][l][1]=min(f[i+1][j][k+1][l][1],tmp+(1<<i));
                    f[i+1][j+1][k][l][1]=min(f[i+1][j+1][k][l][1],tmp+(1<<i));
