  if(a <= l && r <= b) return (sum[R] - sum[L]) ;
  int mid = l + r >> 1 , ans = 0 ;
  if(a <= mid) ans += query(a , b , l , mid , ls[L] , ls[R]) ;
  if(b > mid) ans += query(a , b , mid + 1 , r , rs[L] , rs[R]) ;
  return ans ;
}
