#define maxn 205
using namespace std;
const int inf = 1e8;
struct node {
	int u, v, len;
	bool operator<(const node &x)const {
		return x.len < len;
	}
};
priority_queue <node> q;
int n, m, w[maxn][maxn], dis[maxn][maxn], vis[maxn][maxn];

int main() {
	cin >> n >> m;
	for(int i = 1; i <= n; i++)
		for(int j = 1; j <= n; j++) {
			dis[i][j] = inf;
			if (i != j) w[i][j] = inf;
			else w[i][j] = 0;
		}
	for(int i = 1; i <= m; i++) {
		int x, y;
		cin >> x >> y;
		w[x][y] = 1;
	}
	for(int k = 1; k <= n; k++)
		for(int i = 1; i <= n; i++)
			for(int j = 1; j <= n; j++)
				w[i][j] = min(w[i][j], w[i][k] + w[k][j]);
	dis[1][1] = 1;
	q.push((node){1, 1, 1});
	while(!q.empty()) {
		node temp = q.top();
		q.pop();
		int u = temp.u, v = temp.v;
		if(vis[u][v]) continue;
		vis[u][v] = 1;
		for(int i = 1; i <= n; ++i)
			for(int j = 1; j <= n; ++j) {
				if (u == i && v == j) continue;
				if (dis[u][v] + w[i][j] + w[v][i] + w[j][u] - 1 < dis[i][j]) {
					dis[i][j] = dis[u][v] + w[v][i] + w[i][j] + w[j][u] - 1;
					q.push((node){i, j, dis[i][j]});
				}
			}
	}
	printf("%d\n", dis[2][2]);
	return 0;
}
