	dp[1][0] = 1;
	for (register int i = 2;i <= n; ++ i) {
		mst(dp[i & 1ll],0);
		if (a[i] != -1) {
			if (a[i] >= 1) dp[i & 1ll][a[i]] = MOD(dp[i - 1 & 1ll][a[i] - 1] + dp[i - 1 & 1ll][a[i]] + dp[i - 1 & 1ll][a[i] + 1]);
			else dp[i & 1ll][a[i]] = MOD(dp[i - 1 & 1ll][a[i]] + dp[i - 1 & 1ll][a[i] + 1]);
		} else {
			int R = min(i - 1,n - i);
			for (register int j = 0;j <= R; ++ j) {
