using namespace std;
template <class T> inline bool Max(T &x, T y){
	return x<y?x=y,1:0;
}
template <class T> inline bool Min(T &x, T y){
	return (x>y||x==-1)?x=y,1:0;
}
template <class T> inline T rd() {
	static char ch;static bool neg;
	static T x=0;
	for(ch=neg=0;ch<'0' || '9'<ch;neg|=ch=='-',ch=getchar());
	for(x=0;'0'<=ch && ch<='9';(x*=10)+=ch-'0',ch=getchar());
	return x=(neg?-x:x);
}
const int mod=1e9+7,oo=2e9,M=2000+5,N=100000+5;

int n,m;
int C[N],D[N];
struct edge{
	int to,nxt;
}Edge[N<<1];
int head[N],tcnt;
void AddEdge(int u, int v){
	Edge[++tcnt]=(edge)<%v,head[u]%>;
	head[u]=tcnt; return;
}

struct P50{
	inline void solve(){
		int tot=0;
		rep(i,1,n) if(C[i]){
			tot+=max(D[i],0);
			Erep(j,i){
				int y=Edge[j].to;
				D[y]--;
			}
		}
		rep(i,1,n) if(!C[i]){
			tot+=max(D[i],0);
		}
		printf("%d\n",tot);
	}
}p50;
int tmp[2][M*5];
int dp[M][2];
struct P100{
	void dfs(int x, int fa){
		int sum=0;
		Erep(i,x){
			int y=Edge[i].to;
			if(y==fa) continue;
			dfs(y,x);
			sum+=C[y];
		}
		ms(tmp,-1); tmp[0][0]=0;
		int cur=0;
		Erep(i,x){
			int y=Edge[i].to;
			if(y==fa) continue;
			cur^=1; ms(tmp[cur],-1);
			rep(j,0,sum-C[y]){
				if(~tmp[cur^1][j]){
					Min(tmp[cur][j+C[y]],tmp[cur^1][j]+dp[y][0]);//儿子先点,对父亲贡献c[y]
					Min(tmp[cur][j],tmp[cur^1][j]+dp[y][1]);//当前节点先点,儿子并没有对父亲贡献
				}
			}
//        	for(int j=0; j<=sum; j++) printf("tmp[%d][%d] == %d\n",cur,j,tmp[cur][j]);
		}
		dp[x][0]=dp[x][1]=oo;
		rep(i,0,sum){//把这个点当成儿子看待
			if(~tmp[cur][i]){
