#include<cstdio>
#include<algorithm>
#include<cstring>
#include<queue>
#define N 20000
#define M 200000 
using namespace std;
int n,m;
int st[N+1],fst[N+1],tot,ftot;
struct edge
{
	int to,last;
}e[M+1],fe[M+1];
void add(int a,int b)
{
	e[++tot].to=b;
	e[tot].last=st[a];
	st[a]=tot;
}
void fadd(int a,int b)
{
	fe[++ftot].to=b;
	fe[ftot].last=fst[a];
	fst[a]=ftot;
}
queue<int> q;
bool vis1[N+1],vis2[N+1];
void dfs1(int u)
{
	vis1[u]=true;
	for(int i=st[u];i!=0;i=e[i].last)
		if(!vis1[e[i].to])
			dfs1(e[i].to);
}
void dfs2(int u)
{
	vis2[u]=true;
	for(int i=fst[u];i!=0;i=fe[i].last)
		if(!vis2[fe[i].to])
			dfs2(fe[i].to);
}
int in[N+1],f[N+1];
int dis[N+1],pass[N+1],cnt;
bool spfa(int u)//由于作者比较懒，就打了递归版spfa
{
	if(++pass[u]>=cnt)
		return true;
	for(int i=st[u];i!=0;i=e[i].last)
		if(dis[u]+1>dis[e[i].to]&&vis1[e[i].to]&&vis2[e[i].to])
		{
			dis[e[i].to]=dis[u]+1;
			bool res=spfa(e[i].to);
			if(res)
				return true;
		}
	return false;
}
int main()
{
	scanf("%d %d",&n,&m);
	for(int i=1;i<=m;i++)
	{
		int a,b;
		scanf("%d %d",&a,&b);
		add(a,b),fadd(b,a);
	}
	dfs1(1),dfs2(2);
	if(!vis1[2]||!vis2[1])
	{
		printf("0");
		return 0;
	}
	for(int i=1;i<=n;i++)
		cnt+=(vis1[i]&vis2[i]);
	if(spfa(1))
	{
		printf("inf");
		return 0;
	}
	for(int i=1;i<=n;i++)
		if(vis1[i]&&vis2[i])
			for(int j=st[i];j!=0;j=e[j].last)
				if(vis1[e[j].to]&&vis2[e[j].to])
					in[e[j].to]++;
	f[1]=1;
	q.push(1);
	while(!q.empty())
	{
		int u=q.front();
		for(int i=st[u];i!=0;i=e[i].last)
		{
			int v=e[i].to;
			if(vis1[v]&&vis2[v])
			{
				in[v]--;
				f[v]=(f[v]+f[u])%1000000000;
				if(in[v]==0)
					q.push(v);
			}
		}
		q.pop();
	}
	printf("%d",f[2]);
	return 0;
}
