{
    file();
    read(n,K,m);K=n-K+1;
    rep(i,1,n) read(p[i]);
    int c=0,cc=1;
    dp[0][0][0]=1;
    rep(i,1,n)
    {
        swap(c,cc);
        rep(j,0,m) rep(k,0,K) dp[c][j][k]=0;
        dp[c][0][0]=1;
        rep(j,1,p[i]-1) rep(k,1,K) dp[c][j][k]=dp[cc][j][k];
        rep(j,p[i],m) 
            rep(k,1,K) 
                dp[c][j][k]=(dp[cc][j][k]+dp[cc][j-p[i]][k-1]-dp[cc][j-p[i]][k]+mod)%mod;
