const int INF = 0x3f3f3f3f;
const int MOD = 998244353;
int n, m, k, ans, inv[MAXN], a[MAXN];
int dp[MR][MAXN];
int ksm(int a, int b){//a ^ b % p;
	int ans = 1;
	while(b){
		if(b & 1)ans = ans * a % MOD;
		b >>= 1;
		a = a * a % MOD;
	}
	return ans % MOD;
} 
signed main()
{
	cin >> n >> k >> m;
	k = n - k + 1;
	for(int i = 1;i <= n; i++){
		cin >> a[i];
		inv[0] = dp[0][0] = 1;
	}
	for(int i = 1;i <= m; i++){
		inv[i] = ksm(i, MOD - 2);
	}
	for(int i = 1;i <= n; i++){
		for(int l = m;l >= a[i]; l--){
			for(int j = k; j; j--){
				dp[j][l] = (dp[j - 1][l - a[i]] - dp[j][l - a[i]] + dp[j][l] + MOD) % MOD;
			}
		}
	}
	for(int i = 0;i <= m; i++){
		ans = (ans + dp[k][i] * inv[i] % MOD) % MOD;
	}
	cout << ans * m % MOD;
	return 0;
}
