# Code

```cpp
#include <iostream>
#include <cstdio>
#include <cstring>
using namespace std;
#define ll long long
const int MOD=998244353;
int n,k,m,p[1050];
ll dp[10050][15],ans;
ll ksm(ll a,ll b)
{
	ll tmp=1;
	while(b)
	{
		if(b&1)tmp=(1LL*tmp*a)%MOD;
		a=(1LL*a*a)%MOD;
		b>>=1;
	}
	return tmp;
}
int main()
{
	scanf("%d%d%d",&n,&k,&m);k=n-k+1;
	dp[0][0]=1;
	for(int i=1;i<=n;i++)scanf("%d",&p[i]);
	for(int i=1;i<=n;i++)
		for(int j=m;j>=p[i];j--)
			for(int u=1;u<=k;u++)
				dp[j][u]=(dp[j][u]+dp[j-p[i]][u-1]-dp[j-p[i]][u]+MOD)%MOD;
	for(int i=1;i<=m;i++)ans=(ans+1LL*dp[i][k]*ksm(i,MOD-2)%MOD*m%MOD)%MOD; 
	printf("%lld",(ans+MOD)%MOD);
	return 0;
}
```
