```cpp
#include <iostream>
#include <cstdio>
#include <cstring>
#include <cstdlib>
#include <algorithm>
#include <vector>
#define mod 998244353
#define inv3 (mod+1)/3

using namespace std;
typedef long long ll;
const int MAXN = 265005;
inline int readint()
{
	int res = 0, f = 1;
	char c = 0;
	while(!isdigit(c))
	{
		c = getchar();
		if(c=='-')
			f = -1;
	}
	while(isdigit(c))
		res = res*10+c-'0', c = getchar();
	return res*f;
}
int n,a[MAXN],cnt[MAXN];
bool vis[MAXN],flag[MAXN];
ll fac[MAXN];
vector<int> d;
inline ll qpow(ll a, int b)
{
	ll res = 1;
	for(; b; a = a*a%mod, b >>= 1)
		if(b&1)
			res = res*a%mod;
	return res; 
}
inline ll getInv(ll x)
	{ return qpow(x,mod-2); }
inline int gcd(int a, int b)
	{ return b?gcd(b,a%b):a; }
inline int modd(int x)
	{ return x>=mod?x-mod:x; }
ll p[MAXN],q[MAXN],ainv[MAXN];
int rev[MAXN];
inline void ntt(ll *a, int deg, int f)
{
	ll u,t;
	for(int i = 0; i<deg; i++)
		if(i<rev[i])
			swap(a[i],a[rev[i]]); 
	for(int i = 1; i<deg; i <<= 1)
	{
		ll wn = qpow(f,(mod-1)/(i<<1));
		for(int j = 0; j<deg; j += i<<1)
		{
			ll w = 1;
			for(int k = j; k<j+i; k++)
			{
				u = a[k], t = w*a[k+i]%mod;
				a[k] = modd(u+t), a[k+i] = modd(u-t+mod);
				w = w*wn%mod;
			}
		}
	}
	if(f*3%mod==1)
	{
		ll invdeg = getInv(deg);
		for(int i = 0; i<deg; i++)
			a[i] = a[i]*invdeg%mod;	
	}
}
struct Poly
{
	vector<ll> a;
	int deg;
	Poly(int size = 0)
	{
		a.clear();
		deg = size;
		for(int i = 0; i<=size; i++)
			a.push_back(0); 
	}
	inline Poly resize(int n)
	{
		if(deg>n)
			while(a.size()>n+1)
				a.pop_back();
		else
			while(a.size()<n+1)
				a.push_back(0);
		deg = n;
		return *this;
	}
	Poly operator*(const Poly t) const
	{
		int tn = deg, tm = t.deg, n = tn, m = tm;
		m += n;
		int k = 0;
		for(n = 1; n<=m; n <<= 1, k++);
		for(int i = 0; i<n; i++)
			rev[i] = (rev[i>>1]>>1)|((i&1)<<(k-1));
		for(int i = 0; i<n; i++)
			p[i] = i>tn?0:a[i], q[i] = i>tm?0:t.a[i];
		ntt(p,n,3), ntt(q,n,3);
		for(int i = 0; i<n; i++)
			p[i] = p[i]*q[i]%mod;
		ntt(p,n,inv3);
		Poly res(m);
		for(int i = 0; i<=m; i++)
			res.a[i] = p[i];
		return res;
	}
	Poly operator*(const ll t) const
	{
		Poly res(deg);
		for(int i = 0; i<=deg; i++)
			res.a[i] = a[i]*t%mod;
		return res;
	}
	Poly operator+(const Poly t) const
	{
		Poly res(max(deg,t.deg));
		for(int i = 0; i<=res.deg; i++)
			res.a[i] = modd((i>deg?0:a[i])+(i>t.deg?0:t.a[i]));
		return res;
	}
	Poly operator-(const Poly t) const
		{ return (*this)+(t*(mod-1)); }
	inline Poly inv()
	{
		Poly res(deg);
		if(deg==0)
		{
			res.a[0] = getInv(a[0]);
			return res;
		}
		Poly b(deg>>1);
		for(int i = 0; i<=b.deg; i++)
			b.a[i] = a[i];
		b = b.inv();
		res = b*2-b*b*(*this);
		return res.resize(deg); 
	}
	inline Poly deriv()
	{
		Poly res(max(0,deg-1));
		if(deg==0)
			return res;
		for(int i = 0; i<deg; i++)
			res.a[i] = a[i+1]*(i+1)%mod;
		return res;
	}
	inline Poly integ()
	{
		Poly res(deg+1);
		for(int i = 1; i<=deg+1; i++)
			res.a[i] = a[i-1]*ainv[i]%mod;
		return res;
	}
	inline Poly ln()
		{ return (deriv()*inv()).resize(deg).integ().resize(deg); }
	inline Poly exp()
	{
		if(deg==0)
		{
			Poly res(0);
			res.a[0] = 1;
			return res;
		}
		Poly b(deg>>1);
		for(int i = 0; i<=b.deg; i++)
			b.a[i] = a[i];
		b = b.exp().resize(deg);
		Poly tmp(0);
		tmp.a[0] = 1;
		return (b*(tmp-b.ln()+(*this))).resize(deg);
	}
};

int main()
{
	n = readint();
	for(int i = 1; i<=n; i++)
		a[i] = readint();
	for(int i = 1; i<=n; i++)
	{
		if(vis[i])
			continue;
		int pos = i, len = 0;
		while(!vis[pos])
			vis[pos] = true, pos = a[pos], len++;
		cnt[len]++;
	}
	for(int i = 1; i<MAXN; i++)
		ainv[i] = getInv(i);
	fac[0] = 1;
	for(int i = 1; i<=n; i++)
		fac[i] = fac[i-1]*i%mod;
	ll ans = 1;
	for(int i = 1; i<=100000; i++)
	{
		if(!cnt[i])
			continue;
		for(int j = 1; j<=cnt[i]; j++)
			flag[j] = false;
		for(int j = 1; j<=cnt[i]; j++)
			if(gcd(j*i,n)==j)
				flag[j] = true;
		Poly now(cnt[i]);
		for(int j = 1; j<=cnt[i]; j++)
		{
			ll t = qpow(i,j-1)*fac[j-1]%mod;
			now.a[j] = flag[j]?t*getInv(fac[j])%mod:0;
		}
		now = now.exp();
		ans = ans*now.a[cnt[i]]%mod*fac[cnt[i]]%mod;
	}
	cout << ans << endl;
	return 0;
}

