const int N=505;
int n;
int a[N];
int A,B,C;
int cnt[7];
int x,y,z;
int ans,m;
int e[N][4];
int f[N][N][N];
int t;
int query(){
	char s[10];
	scanf("%s",s);
	string Ans="";
	for(int i=0;i<7;i++){
		Ans.push_back(s[i]);
	}
	int mask=1,sum=0;
	for(int i=1;i<7;i++){
		string now="";
		for(int j=0;j<7;j++){
			now.push_back(s[(i+j)%7]);
		}
		if(now==Ans){
			mask|=1<<i;
			sum+=i;
		}
		else if(now>Ans){
			mask=1<<i;
			sum=i;
			Ans=now;
		}
	}
	if(mask!=(mask&(-mask))){
		return -1;
	}
	return sum;
}
int main(){
	int T=read();
	while(T--){
		int x=query();
		if(x<0){
			continue;
		}
		a[++n]=x;
	}
	if(!n){
		printf("0\n");
		return 0;
	}
	for(int i=n+1;i;i--){
		a[i]-=a[i-1];
	}
	for(int i=1;i<=n+1;i++){
		a[i]=((a[i]%7+7)%7);
	}
	for(int i=1;i<=n+1;i++){
		cnt[a[i]]++;
	}
//	for(int i=1;i<=7;i++){
//		cout<<cnt[i]<<endl;
//	}
	while(cnt[1]&&cnt[6]){
		cnt[1]--;
		cnt[6]--;
		ans++;
	}
	A=cnt[1]?cnt[1]:cnt[6];
	x=cnt[1]?1:6;
	while(cnt[2]&&cnt[5]){
		cnt[2]--;
		cnt[5]--;
		ans++;
	}
	B=cnt[2]?cnt[2]:cnt[5];
	y=cnt[2]?2:5;
	while(cnt[3]&&cnt[4]){
		cnt[3]--;
		cnt[4]--;
		ans++;
	}
//	cout<<ans<<endl;
	C=cnt[3]?cnt[3]:cnt[4];
	z=cnt[3]?3:4;
	for(int i=0;i<=7;i++){
		for(int j=0;j<=7;j++){
			for(int k=0;k<=7;k++){
				if(i+j+k&&(i*x+j*y+k*z)%7==0){
					e[m][0]=i;
					e[m][1]=j;
					e[m][2]=k;
					e[m++][3]=i+j+k-1;
				}
			}
		}
	}
	for(int i=0;i<=A;i++){
		for(int j=0;j<=B;j++){
			for(int k=0;k<=C;k++){
				if(i+j+k){
					int tmp=inf;
					for(int x=0;x<m;x++){
						if(i>=e[x][0]&&j>=e[x][1]&&k>=e[x][2]){
							tmp=min(tmp,f[i-e[x][0]][j-e[x][1]][k-e[x][2]]+e[x][3]);
						}
					}
					f[i][j][k]=tmp;
				}
			}
		}
	}
	printf("%d\n",ans+f[A][B][C]);

	return 0;
}

