代码：

```cpp
#include<bits/stdc++.h>
using namespace std;
int kind,T,ans[1000006],n;
namespace Task1{
	char s[1000006];
	int ch,now;
	int check(){
		int d=0,u=0;
		for(int i=1;i<=n;i++){
			if(s[i]=='('){
				d+=1;u+=2;
			}
			else{
				d-=2;u-=1;
				if(d<0)d=0;
				if(u<0)return -1;
			}
		}
		if(d>0)return -1;
		return u;
	}
	void work(){
		scanf("%s",s+1);
		n=strlen(s+1);
		ch=check();
		if(ch==-1){
			printf("impossible\n");
			return;
		}
		for(int i=1;i<=n;i++)ans[i]=0;
//		不可以用memset，在这里他会很慢 
		now=-1;
		if(ch)
		for(int i=n;i>=1;i--){
			if(s[i]=='('){
				ans[i]=now;
				now=-now;
			}
			else ans[i]=2;
			ch--;
			if(!ch)break;
		}
		now=-1;
		for(int i=1;i<=n;i++){
			if(s[i]==')'){
				if(ans[i]==2)break;
				ans[i]=now;
				now=-now;
			}
		}
		for(int i=1;i<=n;i++){
			if(ans[i]==1)printf("R");
			else if(ans[i]==-1)printf("B");
			else printf("G");//注意这里不一定是2，有可能是没打标记的前括号（以我的写法） 
		}
		printf("\n");
	}
	void work1(){
		while(T--)work();
	}
};
namespace Task2{ 
	const int MOD=1e9+7;
	int f[305][305][605];//注意这里最好不要定义到外面，开数组可是要时间的，可能会TLE 
	void work2(){
		f[0][0][0]=1;
		for(int i=0;i<=299;i++){//直接把所有的都跑出来然后应付询问，一次性处理 
			for(int j=0;j<=i;j++)
			for(int k=0;k<=2*i;k++){
				f[i+1][j+1][k+2]=(f[i+1][j+1][k+2]+f[i][j][k])%MOD;//下一个是前括号 
				if(k!=0)//下一个是后括号 
				f[i+1][max(j-2,0)][k-1]=(f[i+1][max(j-2,0)][k-1]+f[i][j][k])%MOD;
			}
			for(int j=0;j<=2*i;j++)
			ans[i+1]=(ans[i+1]+f[i+1][0][j])%MOD;//仅收录有效方案 
		}
		while(T--){
			scanf("%d",&n);
			printf("%d\n",ans[n]);
		}
	}
};
int main(){
	scanf("%d%d",&kind,&T);
	if(kind==1)Task1::work1();
	else Task2::work2();
	return 0;
}
```
