int main(){
	s=read;v=read;
	for(int i=0;i<s;++i)b[i]=read;
	int n=s>>1,m=s-n,S=1<<n;
	for(int i=0;i<n;++i){
		int x=1<<i,y=1<<i,t=1<<i+1;
		while(x||y)
			if(!y||x&&a[y-1]<a[x-1]+b[i])a[--t]=a[--x]+b[i];
			else a[--t]=a[--y];
	}ull *d=b+n;
	for(int i=0;i<m;++i){
		int x=1<<i,y=1<<i,t=1<<i+1;
		while(x||y)
			if(!y||x&&c[y-1]<c[x-1]+d[i])c[--t]=c[--x]+d[i];
			else c[--t]=c[--y];
	}
	for(int i=1<<m,j=0;i--;){
		while(j<S&&a[j]+c[i]<=v)++j;
		t+=j;
	}cout<<t;
	return 0;
}


