**Code：**

```cpp
#include <cstdio>
#include <cstring>
#include <algorithm>
#include <vector>
using namespace std;
#define int long long
vector<int> a, b;
int n, m, A[59], ans;
inline void dfs(int l, int r, int sum, bool sign)
{
	if (sum > m)
		return;
	if (l > r)
	{
		if (!sign)
			a.push_back(sum);
		else
			b.push_back(sum);
		return;
	}
	dfs(l + 1, r, sum + A[l], sign);
	dfs(l + 1, r, sum, sign);
}

signed main()
{
	scanf("%lld%lld", &n, &m);
	for (int i = 1; i <= n; i++)
		scanf("%lld", &A[i]);
	dfs(1, n / 2, 0, 0);
	dfs(n / 2 + 1, n, 0, 1);
	sort(a.begin(), a.end());
	for (int i = 0; i < b.size(); i++)
		ans += upper_bound(a.begin(), a.end(), m - b[i]) - a.begin();
	printf("%lld", ans);
	return 0;
}
```

