-	总体思路是状压dp+最**长**路.贴代码:
```cpp
#include<iostream>
#include<cstdlib>
#include<cstdio>
#include<algorithm>
#include<vector>
#include<queue>
using namespace std;
const int N=18+2,INF=10000*20;
inline int read()
{
    char c=getchar();int num=0;
    while(c<'0'||c>'9')c=getchar();
    while(c>='0'&&c<='9'){num=num*10+c-'0';c=getchar();}
    return num;
}
int imap[N][N];//邻接矩阵
int d[1<<N][N],sy[1<<N][N]={0};//记录搜寻状态
struct miaow
{
    int a,b;
};
int main()
{
    register int n,m;
    n=read();
    m=read();
    for(register int i=0;i<n;++i)
    {
        for(register int j=0;j<n;++j)
        {
            if(i==j)imap[i][j]=0;
            else imap[i][j]=INF;
        }
    }
    for(register int i=0;i<m;++i)
    {
        register int x,y,z;
        x=read();y=read();z=read();
        imap[x][y]=min(imap[x][y],z);
    }
    for(register int i=0;i<(1<<(n));++i)
    {
        for(register int j=0;j<n;++j)
        {
            d[i][j]=0;
        }
    }
    miaow qwe;
    queue<miaow> asd;
    d[1][0]=0;
    sy[1][0]=1;
    qwe.a=1;
    qwe.b=0;
    asd.push(qwe);
    register int ans=0;
    while(!asd.empty())
    {
        qwe=asd.front();
        asd.pop();
        register int i=qwe.a,j=qwe.b;
        register int i2=i;
        sy[i][j]=0;
        register int bj[N]={0};
        for(register int k=n-1;k>=0;--k)
        {
            if(i2>=(1<<k))
            {
                i2-=(1<<k);
                bj[k]=1;
            }
        }
        for(register int k=0;k<n;++k)
        {
            if(!bj[k]&&imap[j][k]!=INF&&d[i][j]+imap[j][k]>d[i+(1<<k)][k])
            {
                d[i+(1<<k)][k]=d[i][j]+imap[j][k];
                if(k==n-1)
                {
                    ans=max(ans,d[i+(1<<k)][k]);
                    break;
                }
                if(!sy[i+(1<<k)][k])
                {
                    qwe.a=i+(1<<k);
                    qwe.b=k;
                    asd.push(qwe);
                    sy[i+(1<<k)][k]=1;
                }
            }
        }
    }
    printf("%d",ans);
    return 0;
}
```
