using namespace std;
typedef long long i64;
const int INF =2147483647;
const int MAXN=2e3+3;
char S[MAXN][MAXN]; int n,F[MAXN][MAXN]; i64 ans;
int qread(){
    int w=1,c,ret;
    while((c=getchar())> '9'||c< '0') w=(c=='-'?-1:1); ret=c-'0';
    while((c=getchar())>='0'&&c<='9') ret=ret*10+c-'0';
    return ret*w;
