int main(){
    n=qread(); up(1,n,i) scanf("%s",S[i]+1);
    dn(n,1,i){
        up(1,n,j) if(S[i][j]=='#')
            F[i][j]=1+min({F[i+1][j],F[i+1][j-1],F[i+1][j+1]}),
