```cpp
// luogu-judger-enable-o2
#include <bits/stdc++.h>
using namespace std;
long long n, m, ans;
double f = 0, eps = 1e-6;
int main() {
	int i;
	scanf("%lld%lld", &n, &m);
	if (n >= 1e7) f = (log(n) + 0.5772156649) / 2;
	else {
		for (i = 2; i <= n * 2; i += 2) f += 1.0 / i;
	}
	ans = m * f - eps;
	printf("%lld\n", ans);
	return 0;
}

```
