### 题目关键

手动模拟看出系数关系。

### Code
```cpp
#include<bits/stdc++.h>
#define lL long long
using namespace std;
const int Mod = 998244353,Nmax = 1000001,Qmax = 10001;

int n,a[Nmax],m,C[2010],yyt,tot=1,iid;
struct Question{int x,y,id;}q[Qmax];
long long ans[Qmax];

bool comp(Question tt,Question yy){
	return tt.x<yy.x;
}

inline void Init(){
	scanf("%d",&n);
	for(int i=1;i<=n;++i){
		scanf("%d",&a[i]);
		a[i] %= Mod;
	}

	scanf("%d",&m);
	for(int i=1;i<=m;++i){
		scanf("%d%d",&q[i].x,&q[i].y);
		q[i].id = i;q[i].x+=1;
	}

	sort(q+1,q+m+1,comp);
}

inline void Doit(){
	C[1] = 1;C[2] = 1;
	for(int i=2;tot<=m;){
		if(q[tot].x==i){
			yyt = q[tot].y;iid = q[tot++].id;
			for(int k=1;k<=i;++k)
				ans[iid] = (ans[iid] + (lL)((lL)C[k] * (lL)a[(yyt-2+k)%n + 1])%Mod)%Mod;
		}
		if(q[tot].x!=i)
			for(int k=(++i);k>1;--k)
				C[k] =(C[k] + C[k-1])%Mod;
	}
}

inline void Prin(){
	for(int i=1;i<=m;++i)
		printf("%lld\n",ans[i]);
}

int main(){
	Init();
	Doit();
	Prin();
	return 0;
}
```
