typedef long long readtype;

/* Header {{{ */
#include <bits/stdc++.h>
using namespace std;

typedef long long var;
typedef long double let;

readtype read() {
  readtype a = 0, c = getchar(), s = 0;
  while (!isdigit(c)) s |= c == '-', c = getchar();
  while (isdigit(c)) a = a * 10 + c - 48, c = getchar();
  return s ? -a : a;
}
/* }}} */

const int N = 2e3 + 1;
const int MOD = 1e9 + 9;

inline int add(int a, int b) { return (a + b >= MOD) ? a + b - MOD : a + b; }
inline int mul(int a, int b) { return (1ll * a * b) % MOD; }

int n, k;
int a[N], b[N];
int num[N];
int lf[N][N], g[N], f[N];
int fac[N], inv[N], invfac[N];

int C(int n, int m);

int main() {
#ifndef ONLINE_JUDGE
  freopen("3622.in", "r", stdin);
  freopen("3622.out", "w", stdout);
#endif
  n = read(), k = read();
  if ((n + k) & 1) return !puts("0");

  inv[0] = inv[1] = 1;
  for (int i = 2; i <= n; ++i) inv[i] = mul(MOD - MOD / i, inv[MOD % i]);
  fac[0] = invfac[0] = 1;
  for (int i = 1; i <= n; ++i) {
    fac[i] = mul(fac[i - 1], i);
    invfac[i] = mul(invfac[i - 1], inv[i]);
  }

  for (int i = 1; i <= n; ++i) a[i] = read();
  sort(a + 1, a + n + 1);
  for (int i = 1; i <= n; ++i) b[i] = read();
  for (int i = 1; i <= n; ++i) {
    for (int j = 1; j <= n; ++j) {
      if (b[j] < a[i]) num[i]++;
    }
  }

  lf[0][0] = 1;
  for (int i = 1; i <= n; ++i) {
    lf[i][0] = lf[i - 1][0];
    for (int j = 1; j <= n; ++j) {
      lf[i][j] = add(lf[i - 1][j], mul(lf[i - 1][j - 1], max(0, num[i] - (j - 1))));
    }
  }

  for (int i = 0; i <= n; ++i) g[i] = mul(lf[n][i], fac[n - i]);

  for (int i = 0; i <= n; ++i) {
    for (int j = i; j <= n; ++j) {
      int tv = mul(C(j, i), g[j]);
      if ((j - i) & 1) f[i] = add(f[i], MOD - tv);
      else f[i] = add(f[i], tv);
    }
  }

  printf("%d\n", f[(n + k) / 2]);
  return 0;
}

int C(int n, int m) {
  return mul(fac[n], mul(invfac[n - m], invfac[m]));
}
