	scanf("%d%d", &M, &k);if(__gcd(M, k) != 1) return !puts("Let's go Blue Jays!");
	z = ceil(sqrt(M));for(int i = 1;i <= z;++i) H[B = B * k % M] = i;T = B;
	for(int i = 2;i <= z;++i) if(H[T = T * B % M]) return !printf("%d\n", i * z - H[T]);
	return !puts("Let's go Blue Jays!");
