using namespace std;
#define N (int)(1e5+10)
int n,m,a[N],c[N],tot=0,siz,belong[N],l=1,r=0,K;
int t[N];
long long ans[N],b[N],d[N]; //不开 long long 见祖宗
struct query {
	int l,r,id;
}q[N];
struct node {
