	}
	return ans;
}
int main(){
	register int n,m,k;
	register short maxa=0;
	n=read();m=read();k=read();
	if(k>14){
		for(register int i=1;i<=m;i++){putchar('0');putchar('\n');}
		return 0;
	}
	block=sqrt(n);
	for(register int i=1;i<=n;i++){
		a[i]=read();
