### 直接上代码

```c
#include<iostream>
#include<cstring>
using namespace std;
char map[2001][2001];//l*l矩阵 
int ans[2001];
int l,q;//开全局变量以便边界判断 
int dfs(int a,int b)//不知不觉深搜写成了暴力(awsl)
{
	int sum1=1,sum2=1;
	int x1=a-1,x2=a+1,y1=b-1,y2=b+1;//横向扩展左右 ；纵向扩展上下 
	while(x1>=1&&x2<=l)//计算横向 
	{
		if(map[x1][b]==map[x2][b]) {sum1+=2;x1--;x2++;}
		else break;
	}
	while(y1>=1&&y2<=l)//计算纵向 
	{
		if(map[a][y1]==map[a][y2]) {sum2+=2;y1--;y2++;}
		else break;
	}
	if(sum1>sum2) return sum1;//比较横纵向回文串长度 
	return sum2;
}
int main()
{
	cin>>l>>q;//正常的输入 
	for(int i=1;i<=l;i++)
	  for(int j=1;j<=l;j++)
	  cin>>map[i][j];//平凡无奇de输入 
	for(int i=1;i<=q;i++)
	{
		int a,b;
		cin>>a>>b;
		ans[i]=dfs(a,b);
	}
	for(int i=1;i<=q;i++)
	cout<<ans[i]<<endl;
	return 0;//华丽谢幕 
}
```
