#include <cstdio>
#include <algorithm>

int n, m, p[4010][4010], len;
char s[4010][2010], str[4010];

void Init(int x)
{
	str[0] = str[1] = '#';
	for (int i = 0; i < n; ++i)
	{
		str[(i << 1) + 2] = s[x][i];
		str[(i << 1) + 3] = '#';
	}
}

void Manacher(int x)
{
	Init(x);
	int right = 0, cur;
	for (int i = 1; i < len; ++i)
	{
		if (i < right)
			p[x][i] = std::min(p[x][(cur << 1) - i], p[x][cur] + cur - i);
		else
			p[x][i] = 1;
		while (str[i + p[x][i]] == str[i - p[x][i]])
			++p[x][i];
		if (p[x][i] + i > right)
		{
			cur = i;
			right = p[x][i] + i;
		}
	}
}

int main()
{
	int x, y;
	scanf("%d%d", &n, &m);
	len = n * 2 + 2;
	for (int i = 1; i <= n; ++i)
	{
		scanf("%s", s[i]);
		for (int j = 1; j <= n; ++j)
			s[j + n][i - 1] = s[i][j - 1];
	}
	for (int i = 1; i <= n << 1; ++i)
		Manacher(i);
	while (m--)
	{
		scanf("%d%d", &x, &y);
		printf("%d\n", std::max(p[x][y << 1] - 1, p[y + n][x << 1] - 1));
	}
	return 0;
}
