    {
        R = C = 2;
        memset(a, 0, sizeof a);
    }

    void init()
    {
        R = C = 2;
        a[1][2] = a[2][1] = 0;
        a[1][1] = a[2][2] = 1;
    }

    friend Matrix operator*(const Matrix& x, const Matrix& y)
    {
        Matrix temp;
        temp.R = x.R, temp.C = y.C;
        for(register int i = 1; i <= temp.R; ++i)
            for(register int j = 1; j <= temp.C; ++j)
                for(register int k = 1; k <= x.C; ++k)
