class Fast_IO
{
    static const int MAX = 1e7;

    char inbuf[MAX], outbuf[MAX];
    char *in, *inend, *out;
    std::stack<char> stk;

    char get_char()
    {
        return in == inend and (inend = (in = inbuf) + fread(inbuf, 1, MAX, stdin), in == inend) ? EOF : *in++;
    }

    void put_char(char c)
    {
        if(out - outbuf == MAX)
            fwrite(outbuf, 1, MAX, stdout), out = outbuf;
        *out ++= c;
    }

public :
