#include<bits/stdc++.h>

using namespace std;

const long long  M=1000000007;

long long a[4][4];
long long ans[4][4];
long long dx[4][4];


void as(int n){
    for(int i=1;i<=n;i++)
     for(int j=1;j<=n;j++)
      dx[i][j]=ans[i][j],ans[i][j]=0;

    for(int i=1;i<=n;i++)
     for(int j=1;j<=n;j++)
      for(int k=1;k<=n;k++)
       ans[i][j]=(ans[i][j]+(a[i][k]*dx[k][j])%M)%M;
}


void df(int n){
    for(int i=1;i<=n;i++)
     for(int j=1;j<=n;j++)
      dx[i][j]=a[i][j],a[i][j]=0;

    for(int i=1;i<=n;i++)
     for(int j=1;j<=n;j++)
      for(int k=1;k<=n;k++)
       a[i][j]=(a[i][j]+(dx[i][k]*dx[k][j])%M)%M;
}

void kla(long long q,long long w){
    while(w)
    {
        if(w%2==1) as(q);
        w/=2;
        df(q);
    }
}


int main(){
    long long n,m=2;
    int t;
    cin>>t;
    while(t--){
        memset(a,0,sizeof(a));
        memset(ans,0,sizeof(ans));
        memset(dx,0,sizeof(dx));
        scanf("%lld",&n);
        if(n==1) {
        	cout<<1<<endl;
        	continue;
        }
        a[2][1]=ans[2][1]=1;
        a[1][2]=ans[1][2]=1;
        a[2][2]=ans[2][2]=1;
        kla(m,n-2);      
        printf("%lld\n",(ans[1][1]+3*ans[2][1])%M);
    }
    return 0;
}


