假设不能有连续的1 
用f[i][0/1]表示选到了i处，第i处为白/黑的方案数
f[i][1] = f[i - 1][0]
f[i][0] = f[i - 1][1] + f[i - 1][0]
*/
#include <cmath>
#include <cstdio>
#include <cstring>
#include <iostream>
#include <algorithm>
using namespace std;

const int A = 1e6 + 10000;
const int B = 1e6 + 11;
const int mod = 1000000007;
const int inf = 0x3f3f3f3f;

inline int read() {
	char c = getchar(); int x = 0, f = 1;
	for( ; !isdigit(c); c = getchar()) if(c == '-') f = -1;
	for( ; isdigit(c); c = getchar()) x = x * 10 + (c ^ 48);
	return x * f;
}

int n, f[A][2];

int main() {
	int T = read();
	while(T--) {
		n = read();
		long long ans = 0;
		f[1][0] = 1, f[1][1] = 0;
		for (int i = 2; i <= n; i++) {
			f[i][1] = f[i - 1][0] % mod;
			f[i][0] = (f[i - 1][1] + f[i - 1][0]) % mod;
		} 
		ans = (f[n][0] + f[n][1]) % mod; 
		f[1][0] = 0, f[1][1] = 1;
		for (int i = 2; i <= n; i++) {
			f[i][1] = f[i - 1][0] % mod;
			f[i][0] = (f[i - 1][1] + f[i - 1][0]) % mod;
		} 
		(ans += f[n][0]) %= mod;
		cout << ans << '\n';
	}
