	for(int x=gc;x>=1;x--){
		if(gc%x) continue;
		int n1=n/x,m1=m/x;
		for(int i=0,c=1;i<=m1/(k+1);i++,c=-c){
			f[x]+=c*C(n1-m1,i)*C(n1-i*(k+1)-1,n1-m1-1);
			f[x]=(f[x]%MOD+MOD)%MOD;
		}
	}
