    for(int i = 2 ; i <= n ; ++i)
        fac[i] = fac[i-1] * i % mod;
}
inline ll pow(ll x , ll y)
{
    ll ans = 1 , base = x;
    while(y)
    {
        if(y&1) ans = ans * base % mod;
        base = base % mod * base % mod;
        y /= 2;
    }
    return ans;
