#include<iostream>
using namespace std;
int tu[505][505],t[505][505];//tu是主图，t是副图
int n,m;
int main()
{
    cin>>n>>m;
    int o=1;
    for(int i=1;i<=n;i++)
    {
        for(int j=1;j<=n;j++)//读图，初始化
        {
