        if(z==0)//顺时针旋转
        {
            for(int j=1;j<=r;j++)
            {
                for(int k=0;k<=j;k++)
                {
                    {
                        tu[x-j][y+k]=t[x-k][y-j];//各种转移，模拟即可
                        tu[x+k][y+j]=t[x-j][y+k];
                        tu[x+j][y-k]=t[x+k][y+j];
                        tu[x-k][y-j]=t[x+j][y-k];
                        tu[x+j][y+k]=t[x-k][y+j];
                        tu[x+k][y-j]=t[x+j][y+k];
                        tu[x-j][y-k]=t[x+k][y-j];
                        tu[x-k][y+j]=t[x-j][y-k];
                    }	
                }
            }
        }
        else//逆时针旋转
        {
            for(int j=1;j<=r;j++)
            {
                for(int k=0;k<=j;k++)
                {
                    {
                    //别着急，慢慢看
                        tu[x-k][y-j]=t[x-j][y+k];
                        tu[x-j][y+k]=t[x+k][y+j];
                        tu[x+k][y+j]=t[x+j][y-k];
                        tu[x+j][y-k]=t[x-k][y-j];
                        tu[x+j][y+k]=t[x+k][y-j];
                        tu[x-k][y+j]=t[x+j][y+k];
                        tu[x-j][y-k]=t[x-k][y+j];
                        tu[x+k][y-j]=t[x-j][y-k];
                    }
                }
            }
        }
        for(int j=1;j<=n;j++)
