#include <set>
#include <map>

using namespace std;

const int N = 1005, M = 2005;
int n, m, c, tot;
double dis[N], edge[M];
int nxt[M], ver[M], head[N];
int cnt[N], val[N];
int score[N];
char vis[N];
queue <int> Q;

struct NZ_Flag {
    int op, a, b, k;
}flag[N];

inline char SPFA() {
    memset(vis, 1, sizeof(vis));
    memset(cnt, 0, sizeof(cnt));
    memset(val, 0, sizeof(val));
    for (int i = 0; i <= n; i++) dis[i] = 1, Q.push(i);
    while (!Q.empty()) {
        int cur = Q.front();
        Q.pop();
        vis[cur] = 0;
        for (int i = head[cur]; i; i = nxt[i]) {
            int to = ver[i];
            double w = edge[i];
            if (dis[to] < dis[cur] * w) {
                dis[to] = dis[cur] * w, val[to] = val[cur] + 1;
                if (val[to] > n)
                    return 1;
                if (!vis[to]) {
                    vis[to] = 1, Q.push(to);
                    cnt[to]++;
                    if (cnt[to] > n)
                        return 1;
                }
            }
        }
    }
    return 0;
}

template <class file>
inline void read(file &ret) {
    ret = 0;
    char ch = getchar();
    while (ch < '0' || ch > '9') ch = getchar();
    while (ch >= '0' && ch <= '9') ret = ret * 10 + ch - '0', ch = getchar();
}

inline void addedge(int x, int y, double z) { 
    ver[++tot] = y, edge[tot] = z;
    nxt[tot] = head[x], head[x] = tot; 
}

inline char check(double mid) {
    memset(head, 0, sizeof(head)), tot = 0;
    for (int i = 1; i <= m; i++) {
        if (flag[i].op == 1)
            addedge(flag[i].b, flag[i].a, flag[i].k - mid);
        else
            addedge(flag[i].b, flag[i].a, 1.0 / (mid + flag[i].k));
    }
    for (int i = 1; i <= n; i++)
        if (score[i])
            addedge(0, i, score[i]), addedge(i, 0, 1.0 / score[i]);
    return SPFA();
}

int main() {
    read(n), read(m), read(c);
    double l = 1e-6, r = 10, ans = -1;
    for (int i = 1; i <= m; i++) {
        read(flag[i].op), read(flag[i].a), read(flag[i].b), read(flag[i].k);
        if (flag[i].op == 1)
            r = min(r, 1.0 * flag[i].k);
    }
    while (c--) {
        int x, y;
        read(x), read(y);
        score[x] = y;
    }
    for (int i = 1; i <= 50; i++) {
        double mid = (l + r) / 2;
        if (check(mid))
            l = ans = mid;
        else
            r = mid;
    }
    printf("%.4lf\n", ans);
    return 0;
}
